import SwiftUI

//Must be struct
struct ContentView: View {
    var body: some View {
        VStack(spacing: 5) {
            HStack {
                Image.init("cat_image")
                    .resizable()
                    .aspectRatio(contentMode: .fit)
                    .clipShape(Circle())
                    .frame(width: 100)
                AsyncImage(
                    url:
                        URL(
                            string: "data:image/jpeg;base64,")
                )
                .frame(width: 100)
                .aspectRatio(contentMode: .fit)
                .clipShape(Circle())
            }
            Text("I am cute")
                .bold()
                .foregroundStyle(.brown)
                .font(.largeTitle)
            Text ("Would you adopt me?")
                .foregroundStyle(.brown)
                .font(.title)
            HStack (spacing: 30) {
                Text("Four months")
                    .font(.title2)
                Text ("Male")
                    .font(.title3)
            }
        }.padding()
    }
}

